      title: "PGA TOUR Champions",
      description: "Senior professional golf tour (50+)",
      icon: Star,
      color: "bg-amber-500",
      route: "#",
      active: false,
    },
    {
      id: "liv",
      title: "LIV Golf",
      description: "Professional team-based golf league",
      icon: Target,
      color: "bg-emerald-500",
      route: "#",
      active: false,
    },
    {
      id: "dp-world",
      title: "DP World Tour",
      description: "European professional golf tour",
      icon: Globe,
      color: "bg-orange-500",
      route: "#",
      active: false,
    },
    {
      id: "korn-ferry",
      title: "Korn Ferry Tour",
      description: "PGA TOUR developmental tour",
      icon: Calendar,
      color: "bg-blue-500",
      route: "#",
      active: false,
    },
  ];

  return (
    <>
      <Authenticated>
        <div className="min-h-screen bg-background">
          {/* Header */}
          <AppHeader title="Tournaments" subtitle="Professional golf tours" />

          {/* Main Navigation */}
          <MainNavigation />

          {/* Breadcrumbs - Desktop Only */}
          <div className="hidden sm:block border-t">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-2">
              <Breadcrumb>
                <BreadcrumbList>
                  <BreadcrumbItem>
                    <BreadcrumbLink href="/">Home</BreadcrumbLink>
                  </BreadcrumbItem>
                  <BreadcrumbSeparator />
                  <BreadcrumbItem>
                    <BreadcrumbPage>Tournaments</BreadcrumbPage>
                  </BreadcrumbItem>
                </BreadcrumbList>
              </Breadcrumb>
            </div>
          </div>

          {/* Main Content */}
          <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
            {/* Welcome Section */}
            <div className="text-center mb-8 md:mb-12">
              <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold mb-2 md:mb-3">
                Select Your Professional Tour
              </h2>
              <p className="text-sm sm:text-base text-muted-foreground px-4">
                Choose from the world's premier professional golf tours
              </p>
            </div>

            {/* Tours Grid */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6">
              {tours.map((tour) => {
                const Icon = tour.icon;
                return (
                  <Card
                    key={tour.id}
                    className={`relative ${
                      tour.active
                        ? "cursor-pointer hover:shadow-lg transition-shadow active:scale-98"
                        : "opacity-60"
                    }`}
                    onClick={() => {
                      if (tour.active && tour.route) {
                        router.push(tour.route);
                      }
                    }}
                  >
                    {!tour.active && (
                      <Badge className="absolute top-4 right-4 bg-secondary text-secondary-foreground">
