    38→  const [selectedTournament, setSelectedTournament] = useState<string>("");
    39→
    40→  // Helper function to extract year from season (e.g., "2024-2025" → 2025)
    41→  const getSeasonEndYear = (season: string) => {
    42→    return parseInt(season.split('-')[1]);
    43→  };
    44→
    45→  // Generate all available seasons dynamically (2015-2026)
    46→  const availableSeasons = (() => {
    47→    const currentYear = new Date().getFullYear();
    48→    const startYear = 2015;
    49→    const endYear = Math.max(currentYear + 1, 2026);
    50→    const seasons = [];
    51→
    52→    for (let year = endYear - 1; year >= startYear; year--) {
