   102→  })) || [];
   103→
   104→  // Get current season year for schedule link
   105→  const getCurrentSeasonYear = () => {
   106→    return getSeasonEndYear(selectedSeason);
   107→  };
   108→
   109→  return (
   110→    <div className="min-h-screen bg-background">
   111→      {/* Header */}
   112→      <header className="border-b">
   113→        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
   114→          <div className="flex justify-between items-center h-16">
   115→            <div className="flex items-center">
   116→              <Button
   117→                variant="ghost"
   118→                size="icon"
   119→                onClick={async () => {
   120→                  await signOut();
   121→                  router.push("/");
   122→                }}
   123→                className="mr-3"
   124→              >
   125→                <LogOut className="h-4 w-4" />
   126→              </Button>
   127→              <div>
   128→                <h1 className="text-2xl font-bold">GolfGod</h1>
   129→                <span className="text-sm text-muted-foreground">PGA Tour Analytics</span>
   130→              </div>
   131→            </div>
   132→            <div className="flex items-center gap-2 sm:gap-4">
   133→              <Button
   134→                className="bg-green-700 hover:bg-green-800 text-white text-xs sm:text-sm px-2 sm:px-4"
   135→                onClick={() => router.push("/")}
   136→              >
   137→                <span className="hidden sm:inline">PGA TOUR</span>
   138→                <span className="sm:hidden">PGA</span>
   139→              </Button>
   140→              <ModeToggle />
   141→              <UserMenu />
   142→            </div>
   143→          </div>
   144→        </div>
   145→      </header>
   146→
   147→      {/* Main Content */}
   148→      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
   149→        {/* Welcome Section */}
   150→        <div className="text-center mb-8 md:mb-12">
   151→          <h2 className="text-2xl sm:text-3xl md:text-4xl font-bold mb-2 md:mb-3">
   152→            What would you like to explore?
   153→          </h2>
   154→          <p className="text-sm sm:text-base text-muted-foreground px-4">
   155→            Select an option below to dive into comprehensive golf analytics
   156→          </p>
   157→        </div>
   158→
   159→        {/* Main Options Grid */}
   160→        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 md:gap-6 mb-8 md:mb-12">
   161→          {/* Card 1: FIND PLAYER */}
   162→          <Card className="hover:shadow-lg transition-shadow">
   163→            <CardHeader className="p-4 sm:p-6">
   164→              <div className="w-12 h-12 sm:w-14 sm:h-14 bg-blue-500 rounded-lg flex items-center justify-center mb-3 sm:mb-4">
   165→                <User className="w-6 h-6 sm:w-7 sm:h-7 text-white" />
   166→              </div>
   167→              <CardTitle className="text-base sm:text-lg">FIND PLAYER</CardTitle>
   168→            </CardHeader>
   169→            <CardContent className="p-4 sm:p-6 pt-0 space-y-3">
   170→              <div>
   171→                <SearchableSelect
   172→                  value={selectedPlayerId}
   173→                  onChange={(value) => {
   174→                    setSelectedPlayerId(value);
   175→                    if (value) {
   176→                      router.push(`/players?playerId=${value}`);
