    65→    year: getSeasonEndYear(selectedSeason),
    66→    limit: 100
    67→  });
    68→
    69→  // Helper function to get flag emoji from country code
    70→  const getFlagEmoji = (countryCode: string): string => {
    71→    if (countryCode === "GB-ENG" || countryCode === "GB-SCT" ||
    72→        countryCode === "GB-NIR" || countryCode === "GB-WLS") {
    73→      return "🇬🇧";
    74→    }
    75→    if (!countryCode || countryCode.length !== 2) {
    76→      return "🏳️";
    77→    }
    78→    try {
    79→      const codePoints = countryCode
    80→        .toUpperCase()
    81→        .split("")
    82→        .map((char) => 127397 + char.charCodeAt(0));
    83→      return String.fromCodePoint(...codePoints);
    84→    } catch {
    85→      return "🏳️";
    86→    }
    87→  };
    88→
